
$("button").tooltip({trigger:"click",placement:"bottom"});function setTooltip(a){$("button").tooltip("hide").attr("data-original-title",a).tooltip("show")}function hideTooltip(){setTimeout(function(){$("button").tooltip("hide")},1000)}var clipboard=new Clipboard("button");clipboard.on("success",function(a){setTooltip("Copied!");hideTooltip()});clipboard.on("error",function(a){setTooltip("Failed!");hideTooltip()});